import React, { useState, useEffect } from 'react';

const Dashboard = () => {
  const [memeOfTheDay, setMemeOfTheDay] = useState(null);

  useEffect(() => {
    // Fetch memes from the API and filter for Kenyan-themed memes
    fetch('https://api.imgflip.com/get_memes')
      .then((response) => response.json())
      .then((data) => {
        const allMemes = data.data.memes;
        const kenyanMemes = allMemes.filter(meme => meme.name.toLowerCase().includes("kenya"));
        const randomIndex = getDailyIndex(kenyanMemes.length);
        setMemeOfTheDay(kenyanMemes[randomIndex]);
      });
  }, []);

  const getDailyIndex = (length) => {
    const today = new Date();
    const dayOfYear = Math.floor(
      (today - new Date(today.getFullYear(), 0, 0)) / 1000 / 60 / 60 / 24
    );
    return dayOfYear % length;
  };

  const getGreeting = () => {
    const hour = new Date().getHours();
    if (hour < 12) return 'Good morning, ready for some laughs?';
    if (hour < 18) return 'Good afternoon, need a laugh break?';
    return 'Good evening, unwind with some memes!';
  };

  return (
    <div className="dashboard">
      {/* Header with Navigation Bar */}
      <header className="header">
        <img src="%PUBLIC_URL%/logonew.jpg" alt="Logo" className="logo" />
        <nav className="nav-bar">
          <a href="/">Dashboard</a>
          <a href="/MemeGenerator">Meme Generator</a>
          <a href="/chat">Chat with Us</a>
        </nav>
      </header>

      {/* Main Content */}
      <h1>{getGreeting()}</h1>
      <p>
        Welcome to <strong>MEMES254KE</strong>, a brand that shares the funniest and most relatable Kenyan memes! 
        Founded by a group of friends passionate about spreading joy and humor, MEMES254KE celebrates Kenyan culture, 
        language, and everyday experiences. Our authors and founders take pride in crafting and curating content 
        that will keep you laughing!
      </p>

      {/* Meme of the Day Section */}
      <div className="highlights">
        <h2>Meme of the Day</h2>
        {memeOfTheDay ? (
          <div className="meme-of-the-day">
            <img src={memeOfTheDay.url} alt={memeOfTheDay.name} className="meme-image" />
            <p className="meme-caption">{memeOfTheDay.name}</p>
          </div>
        ) : (
          <p>Loading Meme of the Day...</p>
        )}
      </div>

      {/* Social Media Links */}
      <div className="social-links">
        <h2>Follow Us</h2>
        <a href="https://facebook.com/yourpage" className="social-icon">Facebook</a>
        <a href="https://instagram.com/yourpage" className="social-icon">Instagram</a>
        <a href="https://twitter.com/yourpage" className="social-icon">Twitter</a>
      </div>

      {/* Footer */}
      <footer className="footer">
        <p>&copy; 2024 MEMES254KE. All rights reserved.</p>
        <p>Enjoy our daily dose of laughter and share with friends!</p>
      </footer>

      {/* Inline CSS */}
      <style jsx>{`
        .dashboard {
          font-family: 'Roboto', sans-serif;
          background: linear-gradient(to bottom, #ffefba, #ffffff);
          padding: 20px;
          border-radius: 10px;
          box-shadow: 0 0 20px rgba(0, 0, 0, 0.1);
          margin: 20px;
          text-align: center;
        }
        .header {
          display: flex;
          justify-content: space-between;
          align-items: center;
          padding-bottom: 10px;
          border-bottom: 2px solid #ddd;
        }
        .logo {
          width: 100px;
        }
        .nav-bar a {
          margin: 0 15px;
          color: #333;
          text-decoration: none;
          font-weight: bold;
          transition: color 0.3s;
        }
        .nav-bar a:hover {
          color: #ff6347;
        }
        h1 {
          font-size: 2rem;
          margin-top: 20px;
        }
        p {
          margin: 10px 0;
          color: #555;
        }
        .highlights {
          margin: 20px 0;
          background-color: #fff;
          border-radius: 10px;
          box-shadow: 0 4px 15px rgba(0, 0, 0, 0.1);
          padding: 20px;
        }
        .meme-of-the-day {
          text-align: center;
        }
        .meme-image {
          width: 100%;
          max-width: 300px;
          border-radius: 10px;
          margin: 10px 0;
        }
        .meme-caption {
          font-size: 1rem;
          color: #777;
        }
        .social-links {
          display: flex;
          justify-content: center;
          gap: 15px;
          margin: 20px 0;
        }
        .social-icon {
          color: #555;
          text-decoration: none;
          font-weight: bold;
          transition: color 0.3s;
        }
        .social-icon:hover {
          color: #000;
        }
        .footer {
          margin-top: 30px;
          font-size: 0.9rem;
          color: #777;
        }
      `}</style>
    </div>
  );
};

export default Dashboard;
