import React from 'react';

const Dashboard = () => {
  // Function to get a greeting based on the time of day
  const getGreeting = () => {
    const hour = new Date().getHours();
    if (hour < 12) return 'Good morning, ready for some laughs?';
    if (hour < 18) return 'Good afternoon, need a laugh break?';
    return 'Good evening, unwind with some memes!';
  };

  return (
    <div className="dashboard">
      <img src="/assets/logonew.jpg" alt="Logo" className="logo" />
      <h1>{getGreeting()}</h1>
      <p>Bringing you the best memes daily!</p>
      
      {/* Highlights Section */}
      <div className="highlights">
        <h2>Meme of the Day</h2>
        <img src="/assets/memeoftheday.jpg" alt="Meme of the Day" className="meme-image" />
        <p>Vote on your favorite meme!</p>
      </div>

      {/* Social Media Links */}
      <div className="social-links">
        <h2>Follow Us</h2>
        <a href="https://facebook.com/yourpage" className="social-icon">Facebook</a>
        <a href="https://instagram.com/yourpage" className="social-icon">Instagram</a>
        <a href="https://twitter.com/yourpage" className="social-icon">Twitter</a>
      </div>

      {/* Navigation Links */}
      <nav className="navigation">
        <a href="/MemeGenerator">Meme Generator</a>
        <a href="/chat">Chat with Us</a>
      </nav>

      {/* Footer */}
      <footer className="footer">
        <p>&copy; 2024 MEMES254KE. All rights reserved.</p>
        <p>Enjoy our daily dose of laughter and share with friends!</p>
      </footer>

      {/* Inline CSS */}
      <style jsx>{`
        .dashboard {
          text-align: center;
          font-family: Arial, sans-serif;
          background: linear-gradient(to bottom, #ffefba, #ffffff);
          padding: 20px;
          border-radius: 10px;
          box-shadow: 0 0 20px rgba(0, 0, 0, 0.1);
          margin: 20px;
        }
        .logo {
          width: 150px;
          margin-bottom: 20px;
          animation: fadeIn 2s;
        }
        h1 {
          font-size: 2rem;
          margin-bottom: 10px;
        }
        .highlights {
          margin: 20px 0;
        }
        .meme-image {
          width: 100%;
          max-width: 300px;
          border-radius: 10px;
          margin: 10px 0;
        }
        .social-links {
          display: flex;
          justify-content: center;
          gap: 15px;
          margin-bottom: 20px;
        }
        .social-icon {
          color: #555;
          text-decoration: none;
          font-weight: bold;
          transition: color 0.3s;
        }
        .social-icon:hover {
          color: #000;
        }
        .navigation a {
          margin: 0 15px;
          color: #333;
          text-decoration: none;
          font-size: 1.1rem;
          transition: color 0.3s;
        }
        .navigation a:hover {
          color: #ff6347;
        }
        .footer {
          margin-top: 30px;
          font-size: 0.9rem;
          color: #777;
        }
        @keyframes fadeIn {
          from { opacity: 0; }
          to { opacity: 1; }
        }
      `}</style>
    </div>
  );
};

export default Dashboard;
