import React, { useState, useEffect } from "react";

const MemeGenerator = () => {
  const [memeList, setMemeList] = useState([]);
  const [randomMeme, setRandomMeme] = useState(null);
  const [topText, setTopText] = useState("");
  const [bottomText, setBottomText] = useState("");

  useEffect(() => {
    fetch("https://api.imgflip.com/get_memes")
      .then((response) => response.json())
      .then((data) => setMemeList(data.data.memes));
  }, []);

  const getRandomMeme = () => {
    const randomIndex = Math.floor(Math.random() * memeList.length);
    setRandomMeme(memeList[randomIndex].url);
  };

  return (
    <div className="meme-generator">
      <header className="header">
        <h1 className="title">Meme Generator</h1>
        <p className="subtitle">Create your own hilarious memes!</p>
      </header>
      <div className="input-container">
        <input
          type="text"
          placeholder="Top Text"
          value={topText}
          onChange={(e) => setTopText(e.target.value)}
          className="input"
        />
        <input
          type="text"
          placeholder="Bottom Text"
          value={bottomText}
          onChange={(e) => setBottomText(e.target.value)}
          className="input"
        />
        <button onClick={getRandomMeme} className="generate-btn">
          Get Random Meme
        </button>
      </div>
      {randomMeme && (
        <div className="meme-output">
          <div className="meme-wrapper">
            <img src={randomMeme} alt="Meme" className="meme-image" />
            <h2 className="meme-text top-text">{topText}</h2>
            <h2 className="meme-text bottom-text">{bottomText}</h2>
          </div>
        </div>
      )}

      {/* Inline CSS for Modern Look */}
      <style jsx>{`
        .meme-generator {
          font-family: 'Roboto', sans-serif;
          background-color: #f9f9f9;
          color: #333;
          display: flex;
          flex-direction: column;
          align-items: center;
          padding: 20px;
          min-height: 100vh;
        }
        .header {
          text-align: center;
          margin-bottom: 20px;
        }
        .title {
          font-size: 2.5rem;
          margin-bottom: 5px;
        }
        .subtitle {
          font-size: 1.1rem;
          color: #777;
        }
        .input-container {
          display: flex;
          flex-direction: column;
          align-items: center;
          margin-bottom: 20px;
        }
        .input {
          width: 100%;
          max-width: 400px;
          padding: 10px;
          margin: 10px 0;
          border: 1px solid #ccc;
          border-radius: 5px;
          font-size: 1rem;
        }
        .generate-btn {
          padding: 10px 20px;
          font-size: 1rem;
          background-color: #007bff;
          color: #fff;
          border: none;
          border-radius: 5px;
          cursor: pointer;
          transition: background-color 0.3s;
        }
        .generate-btn:hover {
          background-color: #0056b3;
        }
        .meme-output {
          display: flex;
          justify-content: center;
          margin-top: 20px;
        }
        .meme-wrapper {
          position: relative;
          text-align: center;
        }
        .meme-image {
          max-width: 100%;
          border-radius: 10px;
          box-shadow: 0 4px 15px rgba(0, 0, 0, 0.1);
        }
        .meme-text {
          position: absolute;
          width: 100%;
          font-size: 2rem;
          font-weight: bold;
          color: white;
          text-shadow: 2px 2px 8px rgba(0, 0, 0, 0.7);
          text-align: center;
          padding: 0 10px;
        }
        .top-text {
          top: 10px;
        }
        .bottom-text {
          bottom: 10px;
        }
      `}</style>
    </div>
  );
};

export default MemeGenerator;
