import React, { useState, useEffect } from 'react';

const Dashboard = () => {
  const [memeOfTheDay, setMemeOfTheDay] = useState(null);

  useEffect(() => {
    // Fetch memes from the API and select one at random
    fetch('https://api.imgflip.com/get_memes')
      .then((response) => response.json())
      .then((data) => {
        const memes = data.data.memes;
        const randomIndex = getDailyIndex(memes.length);
        setMemeOfTheDay(memes[randomIndex]);
      });
  }, []);

  // Function to get a consistent random index each day
  const getDailyIndex = (length) => {
    const today = new Date();
    const dayOfYear = Math.floor(
      (today - new Date(today.getFullYear(), 0, 0)) / 1000 / 60 / 60 / 24
    );
    return dayOfYear % length;
  };

  const getGreeting = () => {
    const hour = new Date().getHours();
    if (hour < 12) return 'Good morning, ready for some laughs?';
    if (hour < 18) return 'Good afternoon, need a laugh break?';
    return 'Good evening, unwind with some memes!';
  };

  return (
    <div className="dashboard">
      <img src="./assets/logonew.jpg" alt="Logo" className="logo" />
      <h1>{getGreeting()}</h1>
      <p>Bringing you the best memes daily!</p>

      {/* Highlights Section */}
      <div className="highlights">
        <h2>Meme of the Day</h2>
        {memeOfTheDay ? (
          <div className="meme-of-the-day">
            <img src={memeOfTheDay.url} alt={memeOfTheDay.name} className="meme-image" />
            <p className="meme-caption">{memeOfTheDay.name}</p>
          </div>
        ) : (
          <p>Loading Meme of the Day...</p>
        )}
      </div>

      {/* Social Media Links */}
      <div className="social-links">
        <h2>Follow Us</h2>
        <a href="https://facebook.com/yourpage" className="social-icon">Facebook</a>
        <a href="https://instagram.com/yourpage" className="social-icon">Instagram</a>
        <a href="https://twitter.com/yourpage" className="social-icon">Twitter</a>
      </div>

      {/* Navigation Links */}
      <nav className="navigation">
        <a href="/MemeGenerator">Meme Generator</a>
        <a href="/chat">Chat with Us</a>
      </nav>

      {/* Footer */}
      <footer className="footer">
        <p>&copy; 2024 MEMES254KE. All rights reserved.</p>
        <p>Enjoy our daily dose of laughter and share with friends!</p>
      </footer>

      {/* Inline CSS */}
      <style jsx>{`
        .dashboard {
          text-align: center;
          font-family: 'Roboto', sans-serif;
          background: linear-gradient(to bottom, #ffefba, #ffffff);
          padding: 20px;
          border-radius: 10px;
          box-shadow: 0 0 20px rgba(0, 0, 0, 0.1);
          margin: 20px;
        }
        .logo {
          width: 150px;
          margin-bottom: 20px;
          animation: fadeIn 2s;
        }
        h1 {
          font-size: 2rem;
          margin-bottom: 10px;
        }
        .highlights {
          margin: 20px 0;
          background-color: #fff;
          border-radius: 10px;
          box-shadow: 0 4px 15px rgba(0, 0, 0, 0.1);
          padding: 20px;
        }
        .meme-of-the-day {
          text-align: center;
        }
        .meme-image {
          width: 100%;
          max-width: 300px;
          border-radius: 10px;
          margin: 10px 0;
        }
        .meme-caption {
          font-size: 1rem;
          color: #555;
        }
        .social-links {
          display: flex;
          justify-content: center;
          gap: 15px;
          margin-bottom: 20px;
        }
        .social-icon {
          color: #555;
          text-decoration: none;
          font-weight: bold;
          transition: color 0.3s;
        }
        .social-icon:hover {
          color: #000;
        }
        .navigation a {
          margin: 0 15px;
          color: #333;
          text-decoration: none;
          font-size: 1.1rem;
          transition: color 0.3s;
        }
        .navigation a:hover {
          color: #ff6347;
        }
        .footer {
          margin-top: 30px;
          font-size: 0.9rem;
          color: #777;
        }
        @keyframes fadeIn {
          from { opacity: 0; }
          to { opacity: 1; }
        }
      `}</style>
    </div>
  );
};

export default Dashboard;
