import React, { useState, useEffect } from 'react';

const MemeGenerator = () => {
  const [memeList, setMemeList] = useState([]);
  const [randomMeme, setRandomMeme] = useState(null);
  const [topText, setTopText] = useState("");
  const [bottomText, setBottomText] = useState("");

  useEffect(() => {
    // Fetch memes from the API
    fetch("https://api.imgflip.com/get_memes")
      .then(response => response.json())
      .then(data => setMemeList(data.data.memes));
  }, []);

  const getRandomMeme = () => {
    const randomIndex = Math.floor(Math.random() * memeList.length);
    setRandomMeme(memeList[randomIndex]);
  };

  return (
    <div className="meme-generator">
      {/* Header with Navigation Bar */}
      <header className="header">
        <img src="/assets/logonew.jpg" alt="Logo" className="logo" />
        <nav className="nav-bar">
          <a href="/">Dashboard</a>
          <a href="/MemeGenerator">Meme Generator</a>
          <a href="/cha">Chat with Us</a>
        </nav>
      </header>

      {/* Main Content */}
      <h1>Meme Generator</h1>
      <p>Create your own hilarious memes with ease!</p>

      {/* Meme Form */}
      <div className="meme-form">
        <input
          type="text"
          placeholder="Top Text"
          value={topText}
          onChange={(e) => setTopText(e.target.value)}
          className="text-input"
        />
        <input
          type="text"
          placeholder="Bottom Text"
          value={bottomText}
          onChange={(e) => setBottomText(e.target.value)}
          className="text-input"
        />
        <button onClick={getRandomMeme} className="generate-button">
          Get Random Meme
        </button>
      </div>

      {/* Display Meme */}
      {randomMeme && (
        <div className="meme-display">
          <div className="meme-wrapper">
            <img src={randomMeme.url} alt="Generated Meme" className="meme-image" />
            <h2 className="meme-text top">{topText}</h2>
            <h2 className="meme-text bottom">{bottomText}</h2>
          </div>
        </div>
      )}

      {/* Inline CSS for consistent theme */}
      <style jsx>{`
        .meme-generator {
          font-family: 'Roboto', sans-serif;
          background: linear-gradient(to bottom, #ffefba, #ffffff);
          padding: 20px;
          border-radius: 10px;
          box-shadow: 0 0 20px rgba(0, 0, 0, 0.1);
          margin: 20px;
          text-align: center;
        }
        .header {
          display: flex;
          justify-content: space-between;
          align-items: center;
          padding-bottom: 10px;
          border-bottom: 2px solid #ddd;
        }
        .logo {
          width: 100px;
        }
        .nav-bar a {
          margin: 0 15px;
          color: #333;
          text-decoration: none;
          font-weight: bold;
          transition: color 0.3s;
        }
        .nav-bar a:hover {
          color: #ff6347;
        }
        h1 {
          font-size: 2.5rem;
          color: #333;
          margin-top: 20px;
        }
        p {
          color: #666;
          margin-bottom: 20px;
        }
        .meme-form {
          margin: 20px 0;
        }
        .text-input {
          width: 80%;
          max-width: 400px;
          padding: 10px;
          margin: 10px 0;
          border: 1px solid #ccc;
          border-radius: 5px;
        }
        .generate-button {
          background-color: #ff6347;
          color: #fff;
          padding: 10px 20px;
          border: none;
          border-radius: 5px;
          font-size: 1rem;
          cursor: pointer;
          transition: background-color 0.3s;
        }
        .generate-button:hover {
          background-color: #e5533d;
        }
        .meme-display {
          margin-top: 20px;
        }
        .meme-wrapper {
          position: relative;
          display: inline-block;
        }
        .meme-image {
          width: 100%;
          max-width: 500px;
          border-radius: 10px;
          box-shadow: 0 4px 15px rgba(0, 0, 0, 0.1);
        }
        .meme-text {
          position: absolute;
          left: 50%;
          transform: translateX(-50%);
          width: 90%;
          text-align: center;
          font-family: 'Impact', 'Arial Black', sans-serif;
          color: white;
          text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.5);
          font-size: 2rem;
        }
        .meme-text.top {
          top: 10px;
        }
        .meme-text.bottom {
          bottom: 10px;
        }
      `}</style>
    </div>
  );
};

export default MemeGenerator;
