// src/components/Chat.jsx
import React, { useState } from 'react';
import './Chat.css'; // Ensure you have a CSS file for styles

const Chat = () => {
    const [message, setMessage] = useState('');
    const [messages, setMessages] = useState([
        { sender: 'Moderator', text: 'How can we help you today?' },
    ]);

    const sendMessage = (e) => {
        e.preventDefault();
        if (message.trim()) {
            setMessages([...messages, { sender: 'You', text: message }]);
            setMessage('');
        }
    };

    return (
        <div className="chat">
            <h1>Chat with Moderators</h1>
            <div className="chat-window">
                {messages.map((msg, index) => (
                    <div key={index} className={msg.sender === 'You' ? 'message you' : 'message moderator'}>
                        <strong>{msg.sender}:</strong> {msg.text}
                    </div>
                ))}
            </div>
            <form onSubmit={sendMessage} className="chat-input">
                <input
                    type="text"
                    value={message}
                    onChange={(e) => setMessage(e.target.value)}
                    placeholder="Type your message..."
                    aria-label="Type your message" // Accessibility improvement
                />
                <button type="submit">Send</button>
            </form>
        </div>
    );
};

export default Chat;
